import React, { useState,useEffect  } from 'react';
import Authenticated from '@/Layouts/Authenticated';

import { Head } from '@inertiajs/inertia-react';
import { Card, Col, Input, Row, Upload, Select, Image, Button} from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import {
 listColor
} from "../Const/colorPalete";
import {timeline
} from "../Const/dummy";
const styleHeader = { 
    color: '#555',
    fontSize: 11
}

import CardStackedBarHorizontalChart from '@/Components/Atoms/CardStackedBarHorizontalChart';
import axios from 'axios';
export default function Welcome(props) {
    const {title,url} = props
    const [imageUrl, setImageUrl] = useState('https://cdn.discordapp.com/attachments/1090254309565935659/1151527673802276938/799ab1bf-8cc2-4350-83a5-c180f17c8d8a.png');
    const [model, setModel] = useState('dreamshaper_8.safetensors [9d40847d]');
    const [prompt, setPrompt] = useState('sexy girl with glasses');
    const [denoising, setDenoising] = useState(0.4);
    const [time, setTime] = useState(5);
    const {Option} = Select
    const [loadings, setLoadings] = useState(false);
    const [image, setImage] = useState('');
    const [listimage, setListimage] = useState([]);
    const handleInputChange = (event) => {
        setImageUrl(event.target.value);
    };
    const handleModelChange = (value) => {
        setModel(value);
    };
    const handlePromptChange = (event) => {
        setPrompt(event.target.value);
    };
    const handleDenoisingChange = (event) => {
        setDenoising(event.target.value);
    };
    const handleTimeOut = (event) => {
        setTime(event.target.value);
    };
    const handleClick = async () => { 
        setLoadings(true)
        try {
        const data = {
            body : `{"model":"${model}","denoising_strength":${denoising},"cfg_scale":4,"steps":30,"sampler":"Euler a","negative_prompt":"(poorly drawn face)++, (dark skin, tan skin)++, (character out of frame)1.3, bad quality, low-res, (monochrome)1.1, (grayscale)1.3, acne, skin blemishes, bad anatomy, text, error, missing fingers, extra limbs, missing limbs, cut off, unrealistic, (asian face)++,  (cropped, lowres, text, watermark, logo, signature, jpeg artifacts, username, artist name, trademark, title, multiple view, Reference sheet, long neck, logo, tattoos)","imageUrl":"${imageUrl}","prompt":"${prompt}"}`
        }
        await axios.post(`${url}/prodia/generate`,data)
        .then(({ data }) => {
            setTimeout(function() {
                axios.post(`${url}/prodia/render/${data?.job}`)
                .then(({ data }) => {
                    console.log(data)
                    setImage(data?.imageUrl)
                    
                    setLoadings(false)
                    const databody = {
                        image : data?.imageUrl
                    }
                    axios.post(`${url}/prodia/insert`,databody)
                    .then(({ data }) => {
                        setListimage(data)
                    })
                })
              }, time * 1000);
            
        })
        
        } catch (error) { 
            console.log(error)
            setLoadings(false)
        }
      };
      useEffect(() => {
        axios.get(`${url}/prodia/getImage`)
        .then(({ data }) => {
            setListimage(data)
            })
        }, [])
        console.log(listimage)
    return (
        <div>
            <Head title={title} />
            <div className="py-3">
                <div className="max-w-full mx-auto sm:px-6 lg:px-8">
                    <div className="bg-white overflow-hidden shadow-sm sm:rounded-lg">
                        <div className='max-full p-2'>
                            <Card showHeader={false} style={{textAlign:'center'}}>
                            <Row>
                                <Col sm={12}>URL</Col>
                                <Col sm={12}><Input value={imageUrl} onChange={handleInputChange} /></Col>
                            </Row>
                            <Row>
                                <Col sm={12}>Model</Col>
                                <Col sm={12}>
                                <Select defaultValue={model} onChange={handleModelChange}>
                                    <Option value='absolutereality_v181.safetensors [3d9d4d2b]'>Absolute Reality</Option>
                                    <Option value='anythingV5_PrtRE.safetensors [893e49b9]'>Anything V5</Option>
                                    <Option value='dreamshaper_8.safetensors [9d40847d]'>Dreamshaper 8</Option>
                                    <Option value='Realistic_Vision_V5.0.safetensors [614d1063]'>Realistic Vision</Option>
                                </Select></Col>
                            </Row>
                            <Row>
                                <Col sm={12}>Prompt</Col>
                                <Col sm={12}><Input value={prompt} onChange={handlePromptChange} /></Col>
                            </Row>
                            <Row>
                                <Col sm={12}>Denoising</Col>
                                <Col sm={12}><Input value={denoising} onChange={handleDenoisingChange} /></Col>
                            </Row>
                            <Row>
                                <Col sm={12}>Set Time Out (Second)</Col>
                                <Col sm={12}><Input value={time} onChange={handleTimeOut} /></Col>
                            </Row>
                                
                                <Button  type="primary" onClick={handleClick} loading={loadings}>Click me</Button>
                            </Card>
                        </div>
                        <div className='max-full p-2'>
                            <Row gutter={[8,8]}>
                                <Col xs={24} sm={24} md={24} lg={24} xl={24}>
                                    <div className='max-full p-2'>
                                        {image&&<Image
                                            src={image}
                                            fallback="data:image/png;base64,"
                                        />}
                                    </div>
                                </Col>
                            </Row>
                        </div>
                        <div className='max-full p-2'>
                            <Row gutter={[8,8]}>
                            {listimage.map((el,i)=>{
                                return (
                                <Col xs={6} sm={6} md={6} lg={6} xl={6}>
                                    <div className='max-full p-2'>
                                        <Image
                                            src={!el.image ? "error" : el.image}
                                            fallback="data:image/png;base64,"
                                        />
                                    </div>
                                </Col>)
                            })}
                                
                            </Row>
                        </div>
                        
                        
                    </div>
                </div>
            </div>
        </div>

    )
}